import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import DisplayAnnouncements from "./DisplayAnnouncements";
import { getAnnouncements } from "../../../store/announcement-slice";
import InfiniteScroll from "react-infinite-scroller";

function Announcement() {
  const dispatch = useDispatch();
  const limit = 1;
  const [page, setPage] = useState(0);
  const [maxPages, setMaxPages] = useState(0);
  const [data, setData] = useState([]);

  async function fetchAnnouncements() {
    const response = await dispatch(
      getAnnouncements(`page=${page}&limit=${limit}`)
    );
    setData((state) => {
      return [...state, ...response.announcements];
    });
  }

  useEffect(() => {
    async function fetchMaxPages() {
      const response = 2;
      setMaxPages(response);
    }
    fetchAnnouncements();
  }, []);

  useEffect(() => {
    fetchAnnouncements();
  }, [page]);

  function loadMore() {
    console.log("hello");
    setPage((state) => {
      return state + 1;
    });
  }

  return (
    <InfiniteScroll
      pageStart={page}
      hasMore={page < maxPages - 1}
      loadMore={loadMore}
      initialLoad={false}
      loader={
        <div className="loader" key={0}>
          Loading ...
        </div>
      }
    >
      <DisplayAnnouncements announcements={data} />
    </InfiniteScroll>
  );
}

export default Announcement;
