import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useSearchParams } from "react-router-dom";
import DisplayAnnouncements from "./DisplayAnnouncements";
import PageList from "./PageList";
import { getAnnouncements } from "../../../store/announcement-slice";

function Announcement() {
  const dispatch = useDispatch();
  const limit = 1;
  const page = useState(0);
  var maxPages = 0;
  const [data, setData] = useState({ announcements: [], maxPages: 0 });

  const pages_list = [...Array(data.maxPages).keys()];

  function handleScroll() {
    let userScrollHeight = window.innerHeight + window.scrollY;
    let windowBottomHeight = document.documentElement.offsetHeight;

    if (userScrollHeight >= windowBottomHeight && page < maxPages) {
      setPage((state) => {
        return state + 1;
      });
    }
  }

  useEffect(() => {
    async function fetchMaxPages() {
      const response = 0;
      maxPages = response;
    }

    fetchMaxPages();
    window.addEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    async function fetchAnnouncements() {
      const response = await dispatch(
        getAnnouncements(`page=${page}&limit=${limit}`)
      );
      setData((state) => {
        [...state, response];
      });
    }

    fetchAnnouncements();
  }, [page]);

  return (
    <div>
      <DisplayAnnouncements announcements={data.announcements} />
      <PageList
        pages_list={pages_list}
        setSearchParams={setSearchParams}
        limit={limit}
      />
    </div>
  );
}

export default Announcement;
