import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { useDispatch } from "react-redux";
import { fetchSubjects } from "../../../store/classes-slice";
import Form, { SingleDropdown } from "../../form/Form";
import styles from "./Search.module.css";

function Search() {
  const location = useLocation();
  const dispatch = useDispatch();
  const [options, setOptions] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await dispatch(fetchSubjects());
      const result = response.map((item) => {
        return { key: item.name, value: item.name, text: item.name };
      });
      setOptions(result);
    }
    fetchData();
  }, []);

  console.log(location.pathname + "/" + "bap");
  return (
    <Form className={styles.container} btnName="Search">
      <SingleDropdown options={options}>Search by Subject</SingleDropdown>
    </Form>
  );
}

export default Search;
