import { createSlice } from "@reduxjs/toolkit";
import * as api from "../api/teacher";

const initialState = { loading: true, teacherInfo: {}, isNew: true };

const teacherSlice = createSlice({
  name: "student",
  initialState,
  reducers: {
    startLoading(state) {
      return { ...state, loading: true };
    },
    endLoading(state) {
      return { ...state, loading: false };
    },
    create(state, action) {
      const { result, token } = action.payload;
      localStorage.setItem("profile", JSON.stringify({ token }));
      return { ...state, teacherInfo: result, loading: false, isNew: true };
    },
    saveTeacherInfo(state, action) {
      const teacherInfo = action.payload;
      return { ...state, teacherInfo, isNew: true };
    },
    logout(state) {
      localStorage.clear();
      return { ...state, ...initialState };
    },
    setOld(state) {
      return { ...state, isNew: false };
    },
  },
});

export const teacherActions = teacherSlice.actions;

export default teacherSlice;
