import React, { useEffect, useState } from "react";
import { SingleDropdown, DynamicDropdown } from "../../form/Form";
import { BasicCard, View, Editable } from "../../form/Profile";
import { useDispatch } from "react-redux";
import { fetchClassesNames, fetchSubjects } from "../../../store/classes-slice";

function SearchResult(props) {
  const { searchResult } = props;
  const dispatch = useDispatch();
  const [prerequisiteOptions, setPrerequisiteOptions] = useState([]);
  const [studentOptions, setStudentOptions] = useState([]);
  const [options, setOptions] = useState([]);
  const [subjectOptions, setSubjectOptions] = useState([]);
  console.log(searchResult);

  useEffect(() => {
    async function fetchClasses() {
      const response = await dispatch(fetchClassesNames());
      const result = response.map((item) => {
        return {
          key: item._id,
          value: item._id,
          text: item.class_name,
        };
      });
      setOptions(result);
    }
    fetchClasses();

    async function fetchAllSubjects() {
      const responseSubjects = await dispatch(fetchSubjects());
      const resultSubjects = responseSubjects?.map((item) => {
        return {
          key: item._id,
          value: item._id,
          text: item.name,
        };
      });
      setSubjectOptions(resultSubjects);
    }
    fetchAllSubjects();

    const prerequisites = searchResult?.class_prerequisites?.map((item) => {
      return {
        key: item.class_id,
        value: item.prerequisite_class_name,
        text: item.class_id,
      };
    });
    setPrerequisiteOptions(prerequisites);

    const student_list = searchResult?.student_list?.map((item) => {
      return {
        key: item.student_id,
        value: item.student_name,
        text: item.student_id,
      };
    });
    setStudentOptions(student_list);
  }, []);

  const view_list = [
    { label: "ID", name: "_id" },
    { label: "Password", name: "password" },
  ];
  const editable_list = [
    { label: "Class Description", name: "class_description", type: "text" },
    // {label: "Teacher Name", name:"teacher_name", type: "text"},
    // {label:, name:, type:},
    // {label:, name:, type:},
    // {label:, name:, type:},
    // {label:, name:, type:},
    // {label:, name:, type:},
  ];

  function prereqChangeHandler(e, { value }) {
    setPrerequisiteOptions((state) => {
      return value;
    });
  }
  console.log(prerequisiteOptions);

  return (
    <BasicCard>
      <DynamicDropdown
        options={options}
        value={prerequisiteOptions}
        onChange={prereqChangeHandler}
      >
        Prerequisites
      </DynamicDropdown>
    </BasicCard>
  );
}

export default SearchResult;
