import React, { useState } from "react";
import styles from "./Profile.module.css";
import { useDispatch } from "react-redux";
import { updateStudent } from "../../../store/student-slice";
import Card, { Editable, View } from "../../form/Profile";

function Profile(props) {
  const dispatch = useDispatch();
  const { student } = props;
  const [viewEditSwitch, setViewEditSwitch] = useState(true);
  const [edit, setEdit] = useState({ ...student });
  function editHandler(e) {
    return setEdit({ ...edit, [e.target.name]: e.target.value });
  }

  function toggleHandler() {
    setViewEditSwitch(() => !viewEditSwitch);
    setEdit({ ...student });
  }

  function saveHandler() {
    dispatch(updateStudent(edit));
  }

  const editable_list = [
    { label: "Name", name: "name", type: "text" },
    { label: "Age", name: "age", type: "number" },
    { label: "Email", name: "email", type: "email" },
    { label: "Password", name: "password", type: "text" },
    { label: "Address", name: "address", type: "text" },
  ];

  const view_list = [
    { label: "GPA", info: student.class_grade },
    { label: "Class Grade", info: student.class_grade },
    { label: "Honors classes taken", info: student.honors_classes_taken },
  ];

  return (
    <div className={styles.container}>
      <Card
        onClick={toggleHandler}
        onSave={saveHandler}
        viewEditSwitch={viewEditSwitch}
      >
        <View label="ID">{student._id}</View>
        {editable_list.map((item) => {
          return (
            <Editable
              viewEditSwitch={viewEditSwitch}
              label={item.label}
              name={item.name}
              type={item.type}
              edit={edit}
              onChange={editHandler}
            />
          );
        })}

        {view_list.map((item) => {
          return <View label={item.label}>{item.info}</View>;
        })}
      </Card>
    </div>
  );
}

export default Profile;
