import React, { useEffect, useState } from "react";
import styles from "./CreateClass.module.css";
import { useDispatch } from "react-redux";
import { newClass } from "../../../store/classes-slice";
import Form, {
  Title,
  Text,
  Dropdown,
  Checkbox,
  Schedule,
} from "../../form/Form";

function CreateClass(props) {
  const prerequisitesRef = React.createRef();
  const [classesList, setClassesList] = useState([]);
  const [form, setForm] = useState({
    class_name: "",
    teacher_name: "",
    class_description: "",
    schedule: {
      Monday: false,
      Tuesday: false,
      Wednesday: false,
      Thursday: false,
      Friday: false,
      Saturday: false,
      Sunday: false,
    },
  });
  const dispatch = useDispatch();

  function submitHandler(e) {
    e.preventDefault();
    const data = {
      class_name: form.class_name,
      teacher_name: form.teacher_name,
      class_description: form.class_description,
      class_prerequisites: prerequisitesRef.current.state.__value,
      schedule: form.schedule,
    };
    dispatch(newClass(data));
  }

  useEffect(() => {
    setClassesList();
  }, []);

  function changeHandler(e) {
    setForm((state) => {
      return { ...state, [e.target.name]: e.target.value };
    });
  }

  function scheduleChangeHandler(e) {
    setForm((state) => {
      return {
        ...state,
        class_description: "",
        schedule: { ...schedule, [e.target.name]: e.target.value },
      };
    });
  }

  return (
    <div className={styles.container}>
      <Form onSubmit={submitHandler} btnName="create">
        <Title>Create Class</Title>
        <Text onChange={changeHandler} name="class_name">
          Class Name
        </Text>
        <Text onChange={changeHandler} name="teacher_name">
          Teacher Name
        </Text>
        <Text onChange={changeHandler} name="class_description">
          Class Description
        </Text>
        <Dropdown ref={prerequisitesRef} options={classesList}>
          Prerequisites
        </Dropdown>
        <Schedule>
          <Checkbox name="Monday">Mo</Checkbox>
          <Checkbox name="Tuesday">Tu</Checkbox>
          <Checkbox name="Wednesday">We</Checkbox>
          <Checkbox name="Thursday">Th</Checkbox>
          <Checkbox name="Friday">Fr</Checkbox>
          <Checkbox name="Saturday">Sa</Checkbox>
          <Checkbox name="Sunday">Su</Checkbox>
        </Schedule>
      </Form>
    </div>
  );
}

export default CreateClass;
