import React from "react";
import { Link } from "react-router-dom";
import styles from "./Nav.module.css";

function Icon(props) {
  const { icon: _Icon } = props;
  return <_Icon className={`${styles.icon} ${styles.nested}`} />;
}

export function Li(props) {
  const { to, icon } = props;
  if (!icon) {
    return (
      <li>
        <Link to={to} className={styles.btn}>
          {props.children}
        </Link>
      </li>
    );
  }

  return (
    <li>
      <Link to={to} className={styles.btn}>
        <Icon icon={icon} />
        <span>{props.children}</span>
      </Link>
    </li>
  );
}

export function Ul(props) {
  const { icon } = props;
  return (
    <li>
      <button className={`${styles.btn} ${styles.nested}`}>
        <Icon className={`${styles.icon} ${styles.nested}`} />
      </button>
      <ul className={styles.flyout}>{props.children}</ul>
    </li>
  );
}

function Nav(props) {
  const { header } = props;
  return (
    <div className={styles.container}>
      <div className={styles.sidebar}>
        <header>{header}</header>

        <ul className={styles.navigation}>{props.children}</ul>
      </div>
    </div>
  );
}

export default Nav;
