import React from "react";
import styles from "./CreateClass.module.css";
import Form, { Title, Text, Dropdown } from "../../form/Form";

function CreateClass(props) {
  const myRef = React.createRef();
  const DUMMY_OPTIONS = [
    { key: "bob", text: "Bob", value: "bob" },
    { key: "steve", text: "Steve", value: "steve" },
  ];

  function onChangeHandler(e, data) {}

  function submitHandler(e) {
    e.preventDefault();
    console.log(myRef.current.state.__value);
  }

  return (
    <div className={styles.container}>
      <Form onSubmit={submitHandler} btnName="create">
        <Title>Create Class</Title>
        <Text>Class Name</Text>
        <Text>Class Description</Text>
        <Dropdown
          ref={myRef}
          onChange={onChangeHandler}
          options={DUMMY_OPTIONS}
        >
          Prerequisites
        </Dropdown>
      </Form>
    </div>
  );
}

export default CreateClass;
